import React, { useState } from "react";
import "./App.css";

const characters = [
  {
    id: 1,
    name: "SpongeBob SquarePants",
    description: "Lives in a pineapple under the sea!",
    image:
      "data:image/png;base64,",
  }, // Local image paths or URLs
  {
    id: 1,
    name: "SpongeBob SquarePants",
    description: "Lives in a pineapple under the sea!",
    image:
      "data:image/png;base64,",
  }, // Local image paths or URLs
  // Add more characters here
];

const CharacterPortrait = ({ character, onDelete, onEdit }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editedName, setEditedName] = useState(character.name);
  const [editedDescription, setEditedDescription] = useState(
    character.description
  );
  const [editedImage, setEditedImage] = useState(character.image);

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleDeleteClick = () => {
    onDelete(character.id);
  };

  const handleSaveClick = () => {
    onEdit(character.id, editedName, editedDescription, editedImage);
    setIsEditing(false);
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    switch (name) {
      case "name":
        setEditedName(value);
        break;
      case "description":
        setEditedDescription(value);
        break;
      case "image":
        setEditedImage(value); // Update image path or URL
        break;
      default:
        break;
    }
  };

  return (
    <div className="character-portrait">
      <img src={editedImage} alt={character.name} />
      <h2>{character.name}</h2>
      <p>{character.description}</p>
      <button onClick={handleEditClick}>Edit</button>
      <button onClick={handleDeleteClick}>Delete</button>
      {isEditing && (
        <form>
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={editedName}
            onChange={handleChange}
          />
          <label htmlFor="description">Description:</label>
          <textarea
            name="description"
            value={editedDescription}
            onChange={handleChange}
          />
          <label htmlFor="image">Image URL:</label>
          <input
            type="text"
            id="image"
            name="image"
            value={editedImage}
            onChange={handleChange}
          />
          <button onClick={handleSaveClick}>Save Changes</button>
        </form>
      )}
    </div>
  );
};

const CharacterList = () => {
  const [characterData, setCharacterData] = useState(characters);

  const handleDelete = (id) => {
    setCharacterData(characterData.filter((character) => character.id !== id));
  };

  const handleEdit = (id, editedName, editedDescription, editedImage) => {
    setCharacterData(
      characterData.map((character) =>
        character.id === id
          ? {
              ...character,
              name: editedName,
              description: editedDescription,
              image: editedImage,
            }
          : character
      )
    );
  };

  return (
    <div className="character-list">
      {characterData.map((character) => (
        <CharacterPortrait
          key={character.id}
          character={character}
          onDelete={handleDelete}
          onEdit={handleEdit}
        />
      ))}
    </div>
  );
};

const App = () => {
  return (
    <div className="app">
      <h1>SpongeBob Character Dashboard</h1>
      <CharacterList />
    </div>
  );
};

export default App;
